//力扣答案, 遍历两次即可 
int candy(int* ratings, int ratingsSize) {
    int left[ratingsSize], i;
    for (i = 0; i < ratingsSize; i++) {
        if (i > 0 && ratings[i] > ratings[i - 1]) {
            left[i] = left[i - 1] + 1;
        } else {
            left[i] = 1;
        }
    }
    int right = 0, ret = 0;
    for (i = ratingsSize - 1; i >= 0; i--) {
        if (i < ratingsSize - 1 && ratings[i] > ratings[i + 1]) {
            right = left[i+1] + 1;
        } else {
            right = 1;
        }
        ret += fmax(left[i], right);
    }
    return ret;
}

